import "./App.css";
import React, { useState } from "react";

function Header(props) {
  return (
    <div>
      <h1 className="text-3xl font-bold underline">Full Stack Development Roadmap- {props.year}</h1>
    </div>
  );
}

/* (using Lists Mapping Type 2)

  <ul>
    {courses.map((course, i) => (
      <li key={i}>{course}</li>
    ))}
  </ul>

*/

//using Lists in React using map function
const courses = [
  "HTML",
  "CSS",
  "JavaScript",
  "React JS",
  "NodeJS",
  "Web Animations",
  "MongoDB",
  "Mongoose",
];

//performing data transformation (using Lists Mapping Type 1)
const courseListObjects = courses.map((course, i) => ({
  id: i,
  title: course,
}));

function Main(props) {

  const [coder, setCoder] = useState("Yes!");

  return (
    <div className="CourseList flex items-center">
      <img
        className=""
        height={400}
        src="https://images.pexels.com/photos/5483077/pexels-photo-5483077.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
        alt="Coding on PC"
      />
      <ul>
        {props.courses.map((course) => (
          <li key={course.id}>{course.title}</li>
        ))}
      </ul>
      <h2 className="text-3xl font-bold">Liked the roadmap, {coder}</h2>
      <button onClick={() => setCoder("No!")} className="bg-green-500 p-2 w-20 rounded ">No</button>
    </div>
  );
}

//Footer component
function Footer(props) {
  return (
    <div>
      <p>&copy; Made with Love by- {props.author}</p>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <Header year={new Date().getFullYear()} />
      <Main courses={courseListObjects} />
      <Footer author="Tarun Singh" />
    </div>
  );
}

export default App;
