import "./App.css";
import React from "react";

function Header(props) {
  return (
    <div>
      <h1>Full Stack Development Roadmap- {props.year}</h1>
    </div>
  );
}

//using Lists in React using map function
const courses = [
  "HTML",
  "CSS",
  "JavaScript",
  "React JS",
  "NodeJS",
  "Web Animations",
  "MongoDB",
  "Mongoose",
];

//performing data transformation (using Lists Mapping Type 1)
const coursListObjects = courses.map((course, i) => ({
  id: i,
  title: course,
}));

/* (using Lists Mapping Type 2)

  <ul>
    {courses.map((course, i) => (
      <li key={i}>{course}</li>
    ))}
  </ul>

*/

//Footer component
function Footer(props) {
  return (
    <div>
      <p>&copy; Made with Love by- {props.author}</p>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <Header year={new Date().getFullYear()} />
      <div className="CourseList">
        <ul>
          {courses.map((course, i) => (
            <li key={course.id}>{course.title}</li>
          ))}
        </ul>
      </div>
      <Footer author="Tarun Singh" />
    </div>
  );
}

export default App;
